import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import { useParams } from "react-router-dom";

function Reservaitem(valor) {
  const [show, setShow] = useState(false);

  //const { id } = useParams();
  const [sala, setFormData] = useState([]);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    async function fetchFormData () {
    
    try {
      
      const response = await axios.get(`http://localhost:2527/api/fndsalas/${valor}`);
      setFormData(response.data);      
    } catch (error) {
      console.error(error);
    }

    };
    fetchFormData();
  },[id]); 

  return (
    <>
      <Button variant="dark" size='lg' onClick={handleShow}>
        Reserve Aqui! 
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title color='#00008B'>Salas Fodas</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <p>Descrição:{sala.descricao}</p>
        <p>N°:{sala.numero}</p>
        <p>Capacidade:{sala.capacidade}</p>
        <p>Valor:{sala.valor}</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Reservaitem;
