import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
//import Cabecalho from '../componentes/cabecalho';
import NavSc from '../componentes/navbar';
import Table from 'react-bootstrap/Table';
import Bootfooter from '../componentes/BSfooter';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import logo5 from '../logo5.png';
import logo6 from '../logo6.png';


function Clientes() {
  
  const { id } = useParams();
  const [tableData, setTableData] = useState([]);
  
  useEffect(() => {
      async function fetchTableData () {
      
      try {
        
        const response = await axios.get('http://localhost:2527/api/clientes');
        console.log('resposta' + response.data);
        setTableData(response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchTableData();
    },[]); 

      async function handDelete(id) {
        try{
            await axios.delete(`http://localhost:2527/api/deltclientes/${id}`);
            alert('deletado com sucesso!');
        } catch (error){
          console.error(error);
        }
      }
  
  return (    

    <Container fluid>
      <Row>
        <Col xs={12}>
        <NavSc />
        </Col>
      </Row>
      
      <Card>
        <Card.Body>
      <Row>
          <Table fluid striped bordered hover variant="light">
          <thead>
            <tr>
              <th>CPF</th>
              <th>Nome</th>
              <th>Cep</th>
              <th>Email</th>
              <th>Update</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
          {tableData.map((row, index) => {
              return (
            <tr key={index} data-toogle="tooltip" title={row.cep}>
                <td>{row.cpf}</td>
                <td>{row.nome}</td>
                <td>{row.cep}</td>
                <td>{row.email}</td>
                <td><a href={`/assalas/${row._id}`}>
                  <img src={logo5} alt='logo5'></img>
                  </a>
                </td>
                <td><a href={`/assalas/${row._id}`}>
                  <img src={logo6} alt='logo6' onClick={handDelete(row._id)}></img>
                  </a>
                </td>
            </tr>
          );
          })}
          </tbody>
           </Table>
           <Button variant="dark" size='lg' type="submit" href='../index'>
              Retornar
            </Button>
      </Row>       
      </Card.Body>
      </Card>

      <Bootfooter/>    

    </Container>
  );
}

export default Clientes;
