import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import Carousel from 'react-bootstrap/Carousel';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './style.css';
import salasService from '../services/salaService';
import Reservaitem from './BSmodaldin';

function Carrocell() {  

    const [tableData, setTableData] = useState([]);

    useEffect(() => {
        async function fetchTableData () {

        try {

          //const dados = await axios.get('http://localhost:2527/api/salas');
          const dados = await salasService.getSalas();
          console.log(dados.data);
          setTableData(dados.data);
        } catch (error) {
          console.error(error);
        }

        };
        fetchTableData();
      },[]); 

  return (

    <Container fluid>

      <Row>
        <Col xs={12}>
        </Col>
      </Row>
      <Row>

        <Card as='div'>            
            <Card.Body>
                <Card.Title></Card.Title>
                <Card.Text as='div'>
                    <Carousel>        
                        {tableData.map((item, index) => (
                        <Carousel.Item key={item._id}>
                            <Link to={`/salas/${item._id}`}>
                            <img className='wow' key={index} src={item.imagem} alt="" />
                            {item.id}
                            </Link> 
                            <Carousel.Caption>
                              <h3>{item.tipo}</h3>
                              <p>NÂ°:{item.numero}</p>
                              <Reservaitem />
                            </Carousel.Caption>
                        </Carousel.Item>
                        ))}
                    </Carousel>  
                </Card.Text>          
            </Card.Body>
        </Card>

      </Row>

    </Container>

  );
}

export default Carrocell;
