import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MDBIcon, MDBBtn } from 'mdb-react-ui-kit';
import { Container, Row, Col} from 'react-bootstrap';
import { useParams } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import NavSc from '../componentes/navbar';
import Bootfooter from '../componentes/BSfooter';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Figure from 'react-bootstrap/Figure';
import { Link } from 'react-router-dom';
import '../componentes/style.css';


function Cadger() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:2527/api/fndsalas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container fluid>

      <NavSc/>

      <Card>
      <Card.Body>
      <Row>
      <Col sm={2}>
      <MDBBtn outline color="dark" floating className='me-4 text-reset' href="../clientes" target='_blank' role='button'>
            <MDBIcon className='fa-solid fa-greater-than' color='warning' fas icon='user-alt'/>
          </MDBBtn>
      </Col>
      <Col sm={5}>
      <MDBBtn outline color="dark" floating className='me-4 text-reset' href="../salas" target='_blank' role='button'>
            <MDBIcon className='fa-solid fa-greater-than' color='warning' fas icon='hospital'/>
          </MDBBtn>
      </Col>
      </Row>
      </Card.Body>
      </Card>
      
      <Bootfooter/>

    </Container>
  );
}

export default Cadger;
