import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col} from 'react-bootstrap';
import { useParams } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import NavSc from '../componentes/navbar';
import Bootfooter from '../componentes/BSfooter';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Figure from 'react-bootstrap/Figure';
import { Link } from 'react-router-dom';
import '../componentes/style.css';
import logo4 from '../logo4.png';


function Cadger() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:2527/api/fndsalas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container fluid>

      <NavSc/>

      <Card>
      <Card.Body>
      <Row>
      <Col sm={2}>
      <Figure>
              <Link to={'/salas'}>
              <img className='wow' key={index} src={item.imgsala} alt="" />
              {item.id}
              </Link> 
          <Figure.Image rounded
            width={200}
            height={800}
            alt="img"
            src={logo4}
          />
        </Figure>
      </Col>
      <Col sm={5}>
      <Figure>
          <Figure.Image rounded
            width={500}
            height={200}
            alt="img"
            src={sala.imgsala}
          />
        </Figure>
      </Col>
      </Row>
      </Card.Body>
      </Card>
      
      <Bootfooter/>

    </Container>
  );
}

export default Cadger;
