import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import { useParams } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';


function Salasmod() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:2527/api/fndsalas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container fluid>
     
      <Row>
      
        <form action="<%= action %>" method="POST">
          <div class="row">
            <div class="col-xs-4">
              <p>Numero:</p>
                <input type="text" name="numero" value={sala.numero} />
            </div>
            <div class="col-xs-4">
              <p>Capacidade:</p>
                <input type="text" name="capacidade" value={sala.capacidade} />
            </div>
            <div class="col-xs-4">  
              <p>Valor:</p>
                <input type="text" name="valor" value={sala.valor} />
            </div>
            <div class="col-xs-4"> 
              <p>Imagem:</p>
                <input type="text" name="imagem" value={sala.imgsala} />
            </div>
            <div class="col-xs-4">
              <p>Descrição:</p>
                <input type="text" name="descricao" value={sala.descricao} />
            </div>
              <input type="submit" value="Salvar" />
          </div>
        </form>
        <img src={sala.imgsala} alt="imagem banco"></img>
     
      </Row>        

    </Container>
  );
}

export default Salasmod;
